#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sat Jul 31 12:50:40 2021@author: fasil"""from flask import Flask, request, render_templatefrom joblib import loadapp = Flask(__name__)model = load('models/DecisionTreeRegressor.save')model2 = load('models/RandomForestRegressor.save')@app.route('/')def home():    return render_template('index.html')@app.route('/prediction', methods=['POST'])def prediction():    option = [[int(x) for x in request.form.values()]]    if option[0][0] == 2:        del option[0][0]        x_test = option        predict = model.predict(x_test)        print(predict)        pred = predict[[0]]    else:        del option[0][0]        x_test = option        predict = model2.predict(x_test)        print(predict)        pred = predict[[0]]    return render_template('index.html', predic_text='Gas Price for the given date is: {} Dollars'.format(pred))if __name__ == "__main__":    app.run(debug=True)